import{o as a,c as n,b as s}from"./app.1a193a2c.js";const t='{"title":"基础","description":"","frontmatter":{},"headers":[{"level":2,"title":"基础","slug":"基础"},{"level":2,"title":"语义化标签","slug":"语义化标签"},{"level":3,"title":"布局类","slug":"布局类"},{"level":3,"title":"文本类","slug":"文本类"},{"level":3,"title":"媒体资源类","slug":"媒体资源类"},{"level":3,"title":"脚本类","slug":"脚本类"},{"level":3,"title":"表格类","slug":"表格类"},{"level":3,"title":"表单类","slug":"表单类"},{"level":3,"title":"Web 组件","slug":"web-组件"},{"level":2,"title":"常用属性","slug":"常用属性"},{"level":3,"title":"全局标签属性","slug":"全局标签属性"},{"level":3,"title":"需要特殊记忆的标签属性","slug":"需要特殊记忆的标签属性"},{"level":2,"title":"emmet 常用速写规则","slug":"emmet-常用速写规则"},{"level":3,"title":"随机文本操作符 Lorem","slug":"随机文本操作符-lorem"},{"level":3,"title":"嵌套操作符","slug":"嵌套操作符"},{"level":3,"title":"属性操作符","slug":"属性操作符"},{"level":2,"title":"可访问性(ARIA)","slug":"可访问性-aria"},{"level":3,"title":"可访问性是什么","slug":"可访问性是什么"},{"level":3,"title":"什么时候需要给网页添加可访问性","slug":"什么时候需要给网页添加可访问性"},{"level":3,"title":"三大特征","slug":"三大特征"},{"level":3,"title":"解决哪些问题","slug":"解决哪些问题"},{"level":2,"title":"Web component","slug":"web-component"},{"level":3,"title":"什么是 Web component","slug":"什么是-web-component"},{"level":3,"title":"作用是什么","slug":"作用是什么"},{"level":3,"title":"主要组成","slug":"主要组成"}],"relativePath":"base/html.md","lastUpdated":1622359518757}',p={},e=s('<h2 id="基础"><a class="header-anchor" href="#基础" aria-hidden="true">#</a> 基础</h2><div class="language-html"><pre><code><span class="token comment">&lt;!-- 文档类型声明 html5 --&gt;</span>\n<span class="token doctype"><span class="token punctuation">&lt;!</span><span class="token doctype-tag">DOCTYPE</span> <span class="token name">html</span><span class="token punctuation">&gt;</span></span>\n\n<span class="token comment">&lt;!-- 网页所有内容 --&gt;</span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>html</span> <span class="token attr-name">lang</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>en<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>\n  <span class="token comment">&lt;!-- head 标签本身并不携带任何信息，它主要是作为盛放其它语义类标签的容器使用 --&gt;</span>\n  <span class="token comment">&lt;!-- head 标签规定了自身必须是 html 标签中的第一个标签，它的内容必须包含一个 title 标签。如果文档作为 iframe，或者有其他方式指定了文档标题时，可以允许不包含 title 标签 --&gt;</span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>head</span><span class="token punctuation">&gt;</span></span>\n    <span class="token comment">&lt;!-- meta 标签是一组键值对，它是一种通用的元信息表示标签 --&gt;</span>\n    <span class="token comment">&lt;!-- 在 head 中可以出现任意多个 meta 标签。一般的 meta 标签由 name 和 content 两个属性来定义。name 表示元信息的名，content 则用于表示元信息的值 --&gt;</span>\n    <span class="token comment">&lt;!-- 所谓元信息，是指描述自身的信息，元信息类标签，就是 HTML 用于描述文档自身的一类标签，它们通常出现在 head 标签中，一般都不会在页面被显示出来 --&gt;</span>\n    <span class="token comment">&lt;!-- 元信息多数情况下是给浏览器、搜索引擎等机器阅读的，有时候这些信息会在页面之外显示给用户，有时候则不会. --&gt;</span>\n\n    <span class="token comment">&lt;!-- 具有 charset 属性的 meta，表示文档的编码格式，非常重要，网页必备 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>meta</span> <span class="token attr-name">charset</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>UTF-8<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n\n    <span class="token comment">&lt;!-- name 为 viewport 的 meta，移动端开发必备的标签，具有以下属性，可全部设置，用逗号隔开 --&gt;</span>\n    <span class="token comment">&lt;!-- width：页面宽度，可以取值具体的数字，也可以是device-width，表示跟设备宽度相等 --&gt;</span>\n    <span class="token comment">&lt;!-- height：页面高度，可以取值具体的数字，也可以是 device-height，表示跟设备高度相等 --&gt;</span>\n    <span class="token comment">&lt;!-- initial-scale：初始缩放比例 --&gt;</span>\n    <span class="token comment">&lt;!-- minimum-scale：最小缩放比例 --&gt;</span>\n    <span class="token comment">&lt;!-- maximum-scale：最大缩放比例 --&gt;</span>\n    <span class="token comment">&lt;!-- user-scalable：是否允许用户缩放 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>meta</span> <span class="token attr-name">name</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>viewport<span class="token punctuation">&quot;</span></span> <span class="token attr-name">content</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>width=device-width, initial-scale=1.0<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n\n    <span class="token comment">&lt;!-- 具有 http-equiv 属性的 meta，表示执行一个命令 --&gt;</span>\n    <span class="token comment">&lt;!-- X-UA-Compatible 值为 IE=edge,chrome=1，默认使用最新的浏览器 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>meta</span> <span class="token attr-name">http-equiv</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>X-UA-Compatible<span class="token punctuation">&quot;</span></span> <span class="token attr-name">content</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>IE=edge<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n\n    <span class="token comment">&lt;!-- 具有 keywords 属性的 meta，表示页面关键字，用于 SEO --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>meta</span> <span class="token attr-name">keywords</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>keywords<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n\n    <span class="token comment">&lt;!-- 具有 description 属性的 meta，表示页面描述，用于 SEO --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>meta</span> <span class="token attr-name">description</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>description<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n\n    <span class="token comment">&lt;!-- title 标签表示文档的标题 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>title</span><span class="token punctuation">&gt;</span></span>网页标题<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>title</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>head</span><span class="token punctuation">&gt;</span></span>\n\n  <span class="token comment">&lt;!-- 网页呈现出来的内容 --&gt;</span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>body</span><span class="token punctuation">&gt;</span></span>\n    <span class="token comment">&lt;!-- 行内元素 --&gt;</span>\n    <span class="token comment">&lt;!-- display:inline; --&gt;</span>\n    <span class="token comment">&lt;!-- 不会独占一行 --&gt;</span>\n    <span class="token comment">&lt;!-- 设置宽高无效 --&gt;</span>\n    <span class="token comment">&lt;!-- 只有左右 margin/padding 有效 --&gt;</span>\n    <span class="token comment">&lt;!-- 宽高为内容撑开 --&gt;</span>\n    <span class="token comment">&lt;!-- 只能容纳其他行内元素或者文本，a 标签可以容纳所有元素 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>a</span> <span class="token attr-name">href</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>/<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>超链接<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>a</span><span class="token punctuation">&gt;</span></span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>span</span><span class="token punctuation">&gt;</span></span>文本<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>span</span><span class="token punctuation">&gt;</span></span>\n\n    <span class="token comment">&lt;!-- 块元素 --&gt;</span>\n    <span class="token comment">&lt;!-- display:block; --&gt;</span>\n    <span class="token comment">&lt;!-- 独占一行 --&gt;</span>\n    <span class="token comment">&lt;!-- 设置宽高有效，宽度默认 100% --&gt;</span>\n    <span class="token comment">&lt;!-- margin、padding 有效 --&gt;</span>\n    <span class="token comment">&lt;!-- 可以容纳所有类型的元素 --&gt;</span>\n    <span class="token comment">&lt;!-- p 标签不能容纳 p 标签 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span><span class="token punctuation">&gt;</span></span>块元素<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>段落<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span>\n\n    <span class="token comment">&lt;!-- 行内块元素 --&gt;</span>\n    <span class="token comment">&lt;!-- display:inline-block; --&gt;</span>\n    <span class="token comment">&lt;!-- 不会独占一行 --&gt;</span>\n    <span class="token comment">&lt;!-- 设置宽高有效，不设置为内容撑开 --&gt;</span>\n    <span class="token comment">&lt;!-- margin、padding 有效 --&gt;</span>\n    <span class="token comment">&lt;!-- 可以容纳所有类型的元素 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>image-narrow.png<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n\n    <span class="token comment">&lt;!-- picture 元素可以根据屏幕的条件为其中的 img 元素提供不同的源 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>picture</span><span class="token punctuation">&gt;</span></span>\n      <span class="token comment">&lt;!-- 这里的 media 属性是 media query，跟 CSS 的 @media 规则一致 --&gt;</span>\n      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>source</span> <span class="token attr-name">srcset</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>image-wide.png<span class="token punctuation">&quot;</span></span> <span class="token attr-name">media</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>(min-width: 600px)<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>image-narrow.png<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>picture</span><span class="token punctuation">&gt;</span></span>\n\n    <span class="token comment">&lt;!-- video 标签兼容处理 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>video</span> <span class="token attr-name">controls</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>controls<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>\n      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>source</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>movie.webm<span class="token punctuation">&quot;</span></span> <span class="token attr-name">type</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>video/webm<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>source</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>movie.ogg<span class="token punctuation">&quot;</span></span> <span class="token attr-name">type</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>video/ogg<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>source</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>movie.mp4<span class="token punctuation">&quot;</span></span> <span class="token attr-name">type</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>video/mp4<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n      You browser does not support video.\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>video</span><span class="token punctuation">&gt;</span></span>\n\n    <span class="token comment">&lt;!-- audio 标签兼容处理 --&gt;</span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>audio</span> <span class="token attr-name">controls</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>controls<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>\n      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>source</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>song.mp3<span class="token punctuation">&quot;</span></span> <span class="token attr-name">type</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>audio/mpeg<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>source</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>song.ogg<span class="token punctuation">&quot;</span></span> <span class="token attr-name">type</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>audio/ogg<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>You browser does not support audio.<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span>\n    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>audio</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>body</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>html</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><h2 id="语义化标签"><a class="header-anchor" href="#语义化标签" aria-hidden="true">#</a> 语义化标签</h2><h3 id="布局类"><a class="header-anchor" href="#布局类" aria-hidden="true">#</a> 布局类</h3><ul><li>div：代表用于没有对应的语义化标签时使用</li><li>header：页头</li><li>footer：页尾</li><li>main：主体内容</li><li>section：一个页面模块</li><li>nav：导航</li><li>aside：侧边栏</li><li>ul/li：无序列表</li></ul><h3 id="文本类"><a class="header-anchor" href="#文本类" aria-hidden="true">#</a> 文本类</h3><ul><li>h1-h6：标题</li><li>hgroup：标题组</li><li>p：段落</li><li>b：强调重要部分</li><li>span：一般用于包裹文字</li></ul><h3 id="媒体资源类"><a class="header-anchor" href="#媒体资源类" aria-hidden="true">#</a> 媒体资源类</h3><ul><li>a：链接</li><li>i：一般用于字体图标</li><li>img：图片</li><li>figure/img/figcaption：图文组合</li><li>audio/source：音频</li><li>video/source：视频</li></ul><h3 id="脚本类"><a class="header-anchor" href="#脚本类" aria-hidden="true">#</a> 脚本类</h3><ul><li>script：脚本</li><li>noscript：脚本不支持时的占位</li><li>canvas：画布</li></ul><h3 id="表格类"><a class="header-anchor" href="#表格类" aria-hidden="true">#</a> 表格类</h3><ul><li>table：表格主体</li><li>caption：表格标题</li><li>thead：表格头部</li><li>tbody：表格内容</li><li>tfoot：表格尾部</li><li>tr：行</li><li>td：单元格</li><li>th：用于 thead 中代替 td</li><li>colgroup/col：不会渲染，多用于集中控制表格中的单元格</li></ul><h3 id="表单类"><a class="header-anchor" href="#表单类" aria-hidden="true">#</a> 表单类</h3><ul><li>form：表单主体</li><li>label：配合表单元素使用，方便聚焦</li><li>input：输入框，包括多种形态</li><li>textarea：文本域</li><li>select/optgroup/option：下拉框</li><li>button：按钮</li></ul><h3 id="web-组件"><a class="header-anchor" href="#web-组件" aria-hidden="true">#</a> Web 组件</h3><ul><li>element</li><li>template</li><li>slot</li></ul><h2 id="常用属性"><a class="header-anchor" href="#常用属性" aria-hidden="true">#</a> 常用属性</h2><h3 id="全局标签属性"><a class="header-anchor" href="#全局标签属性" aria-hidden="true">#</a> 全局标签属性</h3><ul><li>class：类名，可重复</li><li>id：唯一标识符，不可重复</li><li>style：行内样式</li><li>lang：规定语言类型</li><li>tabindex：是否获取焦点 <ul><li>负值表示该元素应该是可聚焦的，但不应通过顺序键盘导航到达</li><li>0 表示元素应通过顺序键盘导航可聚焦和可到达，但其相对顺序由平台约定定义</li><li>正值意味着元素应该可以通过顺序键盘导航进行聚焦和访问;元素聚焦的顺序是 tabindex 的增加值。如果多个元素共享相同的 tabindex，则它们的相对顺序遵循它们在文档中的相对位置</li></ul></li><li>title：表示元素相关信息的文本，鼠标移入元素显示</li><li>contenteditable：可编辑</li><li>draggable：可拖拽</li><li>data-*：自定义属性，常用于储存一些数据</li><li>hidden：浏览器不会呈现此类元素。不得使用此属性隐藏可合法显示的内容</li><li>spellcheck：是否检查拼写错误</li><li>translate：是否能被翻译</li></ul><h3 id="需要特殊记忆的标签属性"><a class="header-anchor" href="#需要特殊记忆的标签属性" aria-hidden="true">#</a> 需要特殊记忆的标签属性</h3><ul><li>img <ul><li>alt：在图片无法呈现时的替代文本（<strong>建议必写</strong>）</li></ul></li><li>script <ul><li>async：异步加载，一旦下载完，渲染引擎就会中断渲染，执行这个脚本</li><li>defer：异步加载，页面正常渲染结束（DOM 结构完全生成，以及其他脚本执行完成），在执行脚本</li><li>type=&quot;module&quot;：ESModule 要引入模块必须加这个</li><li>总结 <ul><li>没有<code>defer</code>或<code>async</code>属性，浏览器会立即下载并执行相应的脚本，并且在下载和执行时页面的处理会停止</li><li>有了<code>defer</code>属性，浏览器会立即下载相应的脚本，在下载的过程中页面的处理不会停止，等到文档解析完成后脚本才会执行</li><li>有了<code>async</code>属性，浏览器会立即下载相应的脚本，在下载的过程中页面的处理不会停止，下载完成后立即执行，执行过程中页面处理会停止</li><li>如果同时指定了两个属性，则会遵从<code>async</code>属性而忽略<code>defer</code>属性</li><li>多个有<code>defer</code>属性的<code>script</code>标签会按照顺序执行</li><li>多个有<code>async</code>属性的<code>script</code>标签不一定按照顺序执行</li><li><code>head</code>标签中的<code>script</code>标签如何不依赖执行顺序，可以设置<code>async</code>属性异步加载并执行</li><li><code>head</code>标签中的<code>script</code>标签最好加上<code>defer</code>属性，这样不会阻塞浏览器，达到优化用户体验的目的</li></ul></li></ul></li><li>textarea <ul><li>wrap：换行模式，默认为 soft <ul><li>hard：在文本到达元素最大宽度的时候，浏览器自动插入换行符(CR+LF)</li><li>soft：在到达元素最大宽度的时候，不会自动插入换行符</li></ul></li></ul></li><li>canvas <ul><li>height/width：设置宽高（<strong>不要使用 css 来控制 canvas 的宽高，会导致 canvas 内容变形</strong>）</li></ul></li><li>input/type=&quot;file&quot; <ul><li>multiple：文件多选</li><li>webkitdirectory：上传文件夹</li><li>accept：限制文件<a href="https://developer.mozilla.org/en-US/docs/Glossary/MIME_type" target="_blank" rel="noopener noreferrer">MIME 类型</a></li></ul></li><li>表单元素 <ul><li>autofocus：自动聚焦（<strong>只会在页面加载时执行一次</strong>）</li></ul></li></ul><h2 id="emmet-常用速写规则"><a class="header-anchor" href="#emmet-常用速写规则" aria-hidden="true">#</a> emmet 常用速写规则</h2><h3 id="随机文本操作符-lorem"><a class="header-anchor" href="#随机文本操作符-lorem" aria-hidden="true">#</a> 随机文本操作符 Lorem</h3><div class="language-html"><pre><code><span class="token comment">&lt;!-- 常规 --&gt;</span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span><span class="token punctuation">&gt;</span></span>Lorem<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n<span class="token comment">&lt;!-- 指定单词数量 --&gt;</span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span><span class="token punctuation">&gt;</span></span>Lorem10<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span><span class="token punctuation">&gt;</span></span>\n  Lorem ipsum dolor sit amet consectetur adipisicing elit. Autem porro enim\n  impedit voluptatibus? Asperiores aut in placeat sed numquam harum nesciunt\n  esse doloremque a vero! Atque quaerat nostrum ad possimus.\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span><span class="token punctuation">&gt;</span></span>\n  Lorem ipsum dolor sit amet consectetur adipisicing elit. Tempora, repellendus!\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><h3 id="嵌套操作符"><a class="header-anchor" href="#嵌套操作符" aria-hidden="true">#</a> 嵌套操作符</h3><ul><li>子代操作符 &gt;</li></ul><div class="language-html"><pre><code>div&gt;div\n</code></pre></div><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><ul><li>乘法操作符 *</li></ul><div class="language-html"><pre><code>ul&gt;li*5\n</code></pre></div><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>ul</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>ul</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><ul><li>分组操作符 ()</li></ul><div class="language-html"><pre><code>(header&gt;nav)+(main&gt;section)+(footer)\n</code></pre></div><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>header</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>nav</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>nav</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>header</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>main</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>section</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>section</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>main</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>footer</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>footer</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><h3 id="属性操作符"><a class="header-anchor" href="#属性操作符" aria-hidden="true">#</a> 属性操作符</h3><ul><li>id 操作符 #</li></ul><div class="language-html"><pre><code>div#test\n</code></pre></div><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span> <span class="token attr-name">id</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>test<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><ul><li>class 操作符 .</li></ul><div class="language-html"><pre><code>div.test\n</code></pre></div><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>test<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><ul><li>属性操作符 []</li></ul><div class="language-html"><pre><code>a[href title target] div[class=&#39;test&#39; id=&quot;test&quot;]\n</code></pre></div><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>a</span> <span class="token attr-name">href</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span><span class="token punctuation">&quot;</span></span> <span class="token attr-name">title</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span><span class="token punctuation">&quot;</span></span> <span class="token attr-name">target</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span><span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>a</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>test<span class="token punctuation">&quot;</span></span> <span class="token attr-name">id</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>test<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><ul><li>数值计算操作符 $</li></ul><div class="language-html"><pre><code><span class="token comment">&lt;!-- 常规 --&gt;</span>\nul&gt;li.item-$*3\n<span class="token comment">&lt;!-- 指定开始数字 $后面紧跟@符号指定开始数字 --&gt;</span>\nul&gt;li.item-$@3*3\n</code></pre></div><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>ul</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>item-1<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>item-2<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>item-3<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>ul</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>ul</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>item-3<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>item-4<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>li</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>item-5<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>li</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>ul</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><ul><li>文本操作符 {}</li></ul><div class="language-html"><pre><code>div{content}\n</code></pre></div><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span><span class="token punctuation">&gt;</span></span>content<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><h2 id="可访问性-aria"><a class="header-anchor" href="#可访问性-aria" aria-hidden="true">#</a> 可访问性(ARIA)</h2><h3 id="可访问性是什么"><a class="header-anchor" href="#可访问性是什么" aria-hidden="true">#</a> 可访问性是什么</h3><p>是为了方便结合屏幕阅读器等辅助工具给障碍人士带来便利的一套机制<a href="https://developer.mozilla.org/zh-CN/docs/Learn/Accessibility/What_is_accessibility" target="_blank" rel="noopener noreferrer">MDN</a></p><h3 id="什么时候需要给网页添加可访问性"><a class="header-anchor" href="#什么时候需要给网页添加可访问性" aria-hidden="true">#</a> 什么时候需要给网页添加可访问性</h3><p>国内目前只有开源的组件库添加了可访问性，据说很多国外的都法律都要求必须给网页添加可访问性</p><p><a href="https://developer.mozilla.org/zh-CN/docs/learn/Accessibility/WAI-ARIA_basics" target="_blank" rel="noopener noreferrer">WAI-ARIA 基础</a></p><h3 id="三大特征"><a class="header-anchor" href="#三大特征" aria-hidden="true">#</a> 三大特征</h3><ul><li>角色（role）：这定义了元素是干什么的</li><li>属性：我们能通过定义一些属性给元素，让他们具备更多的语义</li><li>状态：用于表达元素当前的条件的特殊属性</li></ul><h3 id="解决哪些问题"><a class="header-anchor" href="#解决哪些问题" aria-hidden="true">#</a> 解决哪些问题</h3><ul><li>超出 HTML5 的语义话</li><li>动态内容的更新</li><li>优化键盘的无障碍操作</li><li>非语义控件的语义化</li></ul><h2 id="web-component"><a class="header-anchor" href="#web-component" aria-hidden="true">#</a> Web component</h2><h3 id="什么是-web-component"><a class="header-anchor" href="#什么是-web-component" aria-hidden="true">#</a> 什么是 Web component</h3><p>Web component 是 W3C 正在向 HTML 和 DOM 规范添加的一套功能</p><h3 id="作用是什么"><a class="header-anchor" href="#作用是什么" aria-hidden="true">#</a> 作用是什么</h3><p>作用是为了增加单个 HTML 元素的封装和操作性，提升 html,css,js 的组合逻辑复用能力，可以理解为浏览器原生版本的 Vue</p><h3 id="主要组成"><a class="header-anchor" href="#主要组成" aria-hidden="true">#</a> 主要组成</h3><ul><li>自定义元素 - 定义新 HTML 元素的 API</li><li>影子 DOM - 封装的 DOM 和样式，配以组合化</li><li>HTML 导入 - 将 HTML 文档导入其他文档的声明方法</li><li>HTML 模板 - <code>&lt;template&gt;</code>元素，允许文档包含惰性的 DOM 块</li></ul><p><a href="https://developer.mozilla.org/zh-CN/docs/Web/Web_Components" target="_blank" rel="noopener noreferrer">MDN</a></p>',70);p.render=function(s,t,p,l,o,c){return a(),n("div",null,[e])};export default p;export{t as __pageData};
